import React, {useEffect, useState} from 'react';
import { useParams, useLocation, useNavigate } from "react-router-dom";
import data from './data/data.json';

import { makeStyles } from '@mui/styles';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Divider from '@mui/material/Divider';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';

import swimming from '../static/swimming.jpg';

const useStyles = makeStyles(theme => ({
    fullScreenHeight: {
        minHeight: 'calc(100vh - 56px)'
    }
}));

function ScrollCalendarCard({selected, day, data}) {
    return (
        <>
            <Card sx={{padding: '12px'}}>
                <CardContent sx={{padding: '0'}}>
                    <Typography>{day}</Typography>
                    <Typography>{data.split(' ')[0]}</Typography>
                </CardContent>
            </Card>
            {selected &&
                <Divider sx={{ borderBottomWidth: 5 }}/>
            }
        </>
    )
}

function FacilityViewPage() {
    const classes = useStyles();
    const location = useLocation();

    const [facility, setFacility] = useState('');
    const [sport, setSport] = useState('');
    const [availability, setAvailability] = useState([]);

    const getQuery = () => {
        const newQuery = new URLSearchParams(location.search).get('location');
        setFacility(newQuery);
        console.log(facility);
    }

    const getAvailability = (data) => {
        const currFacilityInfo = data.filter(data => data.name === facility);
        if (currFacilityInfo[0]) {
            setSport(currFacilityInfo[0]['sport']);
            setAvailability(currFacilityInfo[0]['availability']);
        }
        console.log(availability);
    }

    useEffect(() => {
        console.log('get query')
        getQuery();
    }, []);

    useEffect(() => {
        console.log('get availability')
        getAvailability(data);
    }, [facility]);

    const renderHeaderSection = () => {
        return (
            <>
                <Grid container alignItems="flex-start">
                    <Box sx={{ml:1, mb: 2, width: 1}}>
                        <Typography variant="h3">{facility} </Typography>
                    </Box>
                    <Grid container direction="row" justifyContent="space-between" alignItems="flex-start">
                        <Box sx={{ml:2}} >
                            <Typography variant="caption">{sport} </Typography>
                        </Box>
                        <Box>
                            <LocationOnIcon />
                        </Box>
                        <Box sx={{mr: 2, alignContent: 'stretch' }} >
                            <Typography variant="caption" sx={{mb:1}}>More </Typography>
                            <KeyboardArrowDownIcon />
                        </Box>
                    </Grid>
                </Grid>
            </>
        );
    }

    const renderScrollCalendarCard = (day, data) => {
        return (
            <>
                <Card sx={{padding: '12px'}}>
                    <CardContent sx={{padding: '0'}}>
                        <Typography>{day}</Typography>
                        <Typography>{data.split(' ')[0]}</Typography>
                    </CardContent>
                </Card>
            </>
        )
    }

    const renderScrollCalendar = () => {
        return (
            <>
                <Grid container direction="column">
                    <Typography>April 2022</Typography>

                    <Grid container direction="row">
                        {availability.map(function(object, i) {
                            console.log(object);
                            const day = new Date(object.date + ' 2022').toLocaleString('en-us', {weekday:'short'})
                            return renderScrollCalendarCard(day, object.date);
                        })}
                    </Grid>

                </Grid>

            </>
        )
    }

    return (
        <Grid container justifyContent="flex-start" alignItems="center" className={classes.fullScreenHeight}>
            <Box
                component="img"
                sx={{width: 1, maxHeight: '150px'}}
                src={swimming} />
            {renderHeaderSection()}
            <Divider variant="middle" sx={{width: 0.9}}/>
            {renderScrollCalendar()}
        </Grid>
    );
}

export default FacilityViewPage;
