import React, { useEffect, useRef, useState } from 'react';
import { makeStyles, useTheme } from '@mui/styles';
import { styled, alpha } from '@mui/material/styles';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import InputBase from '@mui/material/InputBase';
import SearchIcon from '@mui/icons-material/Search';

const useStyles = makeStyles(theme => ({

}));

const Search = styled('div')(({ theme }) => ({
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    outline: '1px solid darkGrey',
    // backgroundColor: alpha(theme.palette.background.lightGrey, 0.5),
    // '&:hover': {
    //   backgroundColor: alpha(theme.palette.background.lightGrey, 0.75),
    // },
    width: '100%'
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
    color: 'inherit',
    '& .MuiInputBase-input': {
        padding: theme.spacing(0.75, 1),
        // vertical padding + font size from searchIcon
        paddingLeft: `calc(1em + ${theme.spacing(4)})`,
        // transition: theme.transitions.create('width'),
        width: '100%',
    },
}));

function SearchBarV1() {
    // search bar -> click in
    // choose sport or venue will pop up list
    // allow to cancel to go back
    // can make use of state/? to check if is searching

    const classes = useStyles();
    const location = useLocation();
    const navigate = useNavigate();

    const [isSearching, setIsSearching] = useState(false);

    const startSearch = () => {
        console.log("startSearch");
        if (!isSearching) {
            setIsSearching(true);
        }
    }

    if (isSearching) {
        return 'test';
    }

    return (
        <Search onClick={startSearch}>
            <SearchIconWrapper>
                <SearchIcon />
            </SearchIconWrapper>
            <StyledInputBase
                placeholder="Search Facilities"
                inputProps={{ 'aria-label': 'search' }}
            />
        </Search>
    );
}

export default SearchBarV1;
