import React, {useEffect, useState} from 'react';
import { makeStyles } from '@mui/styles';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import data from './data/data.json';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import { CardActionArea } from '@mui/material';
import swimming from '../static/swimming.jpg';
import { useParams, useLocation, useNavigate } from "react-router-dom";

const useStyles = makeStyles(theme => ({
    fullScreenHeight: {
        minHeight: "100vh"
    }
}));

function ResultPage() {
    const classes = useStyles();
    const location = useLocation();
    const navigate = useNavigate();
    const versionId = new URLSearchParams(location.search).get('version') || 1;
    const [filteredData, setFilteredData] = useState([]);

    const getQuery = () => {
        return new URLSearchParams(location.search).get('query');
    }

    const filterData = (data) => {
        const query = getQuery();
        console.log(query);
        const tempData = data.filter((data) => {
            data.sport.includes(query) || data.name.includes(query)
        })
        console.log(tempData)
        setFilteredData(tempData);
    }

    useEffect(() => {
        filterData(data);
    }, [])

    const goToFacilityViewPage = loc => {
        navigate(`/facilities/view?version=${versionId}&location=${loc}`);
    };

    const renderCard = data => {
        return (
            <Card sx={{ maxWidth: 220, m:1}}>
                <CardActionArea onClick={() => goToFacilityViewPage(data.name)}>
                    <CardMedia
                        component="img"
                        height="90"
                        src = {swimming}
                        alt={data.sport}
                    />
                    <CardContent>
                    <Typography variant="caption" color="text.secondary">
                        {data.sport}
                    </Typography>
                    <Typography gutterBottom variant="caption" component="div">
                        {data.name}
                    </Typography>
                    <Typography variant="caption" color="text.secondary">
                        0.0km
                    </Typography>
                    </CardContent>
                </CardActionArea>
            </Card>
        );
    }

    return (
        <Grid container alignItems="center" className={classes.fullScreenHeight}>
            {/* <Typography>Oh no, the page canâ€™t be found</Typography> */}
            {filteredData && filteredData.map(item => renderCard(item))}
            {!filteredData && <Typography>There are no results found.</Typography>}
        </Grid>
    );
}

export default ResultPage;
