import React, { useEffect, useState } from 'react';
import { makeStyles, useTheme } from '@mui/styles';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import RecentSearch from '../components/RecentSearch';
import SearchBar from '../components/SearchBar';
import AppButton from '../components/AppButton';

const useStyles = makeStyles(theme => ({
    mr1: {
        marginRight: `${theme.spacing(1)} !important`
    },
    mt1: {
        marginTop: `${theme.spacing(1)} !important`
    },
    mx2: {
        marginLeft: `${theme.spacing(2)} !important`,
        marginRight: `${theme.spacing(2)} !important`
    },
    px2: {
        paddingLeft: '2rem !important',
        paddingRight: '2rem !important'
    }
}));

function SearchPage({ isOldVersion, isSearching, recentSearchList, startSearch, cancelSearch, doSearch, removeRecentSearch }) {
    const theme = useTheme();
    const classes = useStyles();

    const doSearchAndUpdateList = item => {
        doSearch(item);
        setRecentSearchList(JSON.parse(window.localStorage.getItem('recentSearchList')));
    }

    const removeRecentSearchAndUpdateList = item => {
        removeRecentSearch(item);
        setRecentSearchList(JSON.parse(window.localStorage.getItem('recentSearchList')));
    }

    return (
        <Grid container alignItems="flex-start" justifyContent="flex-start">
            {isOldVersion && (
                <SearchBar 
                    isSearching={isSearching} 
                    startSearch={startSearch} 
                    cancelSearch={cancelSearch}
                    doSearch={doSearchAndUpdateList}
                />
            )}
            {isOldVersion && isSearching && (
                <Grid container item className={`${classes.mt1} ${classes.mx2}`}>
                    <AppButton content="Sports" className={`${classes.px2} ${classes.mr1}`} />
                    <AppButton content="Venue" className={`${classes.px2} ${classes.mr1}`} />
                </Grid>
            )}
            {isSearching && recentSearchList && recentSearchList.length > 0 && (
                <Grid container item className={classes.mt1}>
                    <RecentSearch 
                        recentSearchList={recentSearchList} 
                        removeRecentSearch={removeRecentSearchAndUpdateList}
                        doRecentSearch={doSearch}
                    />
                </Grid>
            )}
        </Grid>
    );
}

export default SearchPage;
