import React, { useEffect, useRef, useState } from 'react';
import { makeStyles, useTheme } from '@mui/styles';
import { styled, alpha } from '@mui/material/styles';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import Grid from '@mui/material/Grid';
import InputBase from '@mui/material/InputBase';
import SearchIcon from '@mui/icons-material/Search';
import { Typography } from '@mui/material';

const useStyles = makeStyles(theme => ({
    icon: {
        color: theme.palette.background.darkGrey
    }
}));

const Search = styled('div')(({ theme }) => ({
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    outline: `1px solid ${theme.palette.background.midGrey}`,
    // backgroundColor: alpha(theme.palette.background.lightGrey, 0.5),
    // '&:hover': {
    //   backgroundColor: alpha(theme.palette.background.lightGrey, 0.75),
    // },
    width: '100%'
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
    color: theme.palette.background.darkGrey,
    '& .MuiInputBase-input': {
        padding: theme.spacing(0.75, 1),
        // vertical padding + font size from searchIcon
        paddingLeft: `calc(1em + ${theme.spacing(4)})`,
        // transition: theme.transitions.create('width'),
        width: '100%',
    },
}));

function SearchBarV1({ isSearching, startSearch }) {
    // search bar -> click in
    // choose sport or venue will pop up list
    // allow to cancel to go back
    // can make use of state/? to check if is searching

    const classes = useStyles();
    const location = useLocation();
    const navigate = useNavigate();


    return (
        <Grid container justifyContent="space-between" alignItems="center">
            <Grid item xs={isSearching ? 10 : 12}>
                <Search onClick={startSearch}>
                    <SearchIconWrapper>
                        <SearchIcon fontSize="large" className={classes.icon} />
                    </SearchIconWrapper>
                    <StyledInputBase
                        fullWidth
                        placeholder="Search Facilities"
                        inputProps={{ 'aria-label': 'search' }}
                    />
                </Search>
            </Grid>
            <Grid item>
                {isSearching && (
                    <Button variant="text">Cancel</Button>
                )}
            </Grid>
        </Grid>
    );
}

export default SearchBarV1;
