import React, { useState, useEffect } from 'react';
import { makeStyles } from '@mui/styles';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import data from './data/data.json';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import { CardActionArea } from '@mui/material';

const useStyles = makeStyles(theme => ({
    fullScreenHeight: {
        minHeight: "100vh"
    }
}));

function ResultPage() {
    const classes = useStyles();

    const getQuery = () => {
        let search = window.location.search;
        let params = new URLSearchParams(search);
        let venue = params.get('query');
        return venue;
    }

    const renderCard = data => {
        return (
            <Card sx={{ maxWidth: 345 }}>
                <CardActionArea>
                    <CardMedia
                        component="img"
                        height="140"
                        image="/static/images/cards/contemplative-reptile.jpg"
                        alt="green iguana"
                    />
                    <CardContent>
                    <Typography variant="body2" color="text.secondary">
                        {data.sport}
                    </Typography>
                    <Typography gutterBottom variant="h5" component="div">
                        {data.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        Lizards are a widespread group of squamate reptiles, with over 6,000
                        species, ranging across all continents except Antarctica
                    </Typography>
                    </CardContent>
                </CardActionArea>
            </Card>
        );
    }

    return (
        <Grid container alignItems="center" className={classes.fullScreenHeight}>
            {/* <Typography>Oh no, the page canâ€™t be found</Typography> */}
            {data && data.map(item => renderCard(item))}
            {!data && <Typography>There are no results found.</Typography>}
        </Grid>
    );
}

export default ResultPage;
