import React, { useEffect, useState } from 'react';
import { useParams } from "react-router-dom";
import { makeStyles, useTheme } from '@mui/styles';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import SearchPage from './SearchPage';

const useStyles = makeStyles(theme => ({
    container: {
        minHeight: 'calc(100vh - 56px)',
        paddingTop: theme.spacing(2)
    }
}));

function FacilitiesPage() {
    const theme = useTheme();
    const classes = useStyles();

    const { version } = useParams();
    const isOldVersion = (version == 1) || (version == undefined);
    // console.log('SearchPage', version, isOldVersion);

    const [isSearching, setIsSearching] = useState(false);

    useEffect(() => {
        if (window.localStorage.getItem('recentSearchList') === null) {
            window.localStorage.setItem('recentSearchList', JSON.stringify([]));
        }
    }, []);

    const startSearch = () => {
        setIsSearching(true);
    };

    const cancelSearch = () => {
        setIsSearching(false);
    };

    const updateRecentSearch = (newSearch) => {
        const recentSearchList = JSON.parse(window.localStorage.getItem('recentSearchList'));
        recentSearchList.push(newSearch);
        window.localStorage.setItem('recentSearchList', JSON.stringify(recentSearchList));
    };

    const doSearch = input => {
        updateRecentSearch(input);
    };

    const removeRecentSearch = item => {
        console.log('removeRecentSearch', item)
        const newRecentSearchList = JSON.parse(window.localStorage.getItem('recentSearchList'));
        console.log('OLD', newRecentSearchList?.length);
        const newu = newRecentSearchList.filter(e => e == 'dd');
        console.log('DELETE', newu?.length);
        window.localStorage.setItem('recentSearchList', JSON.stringify(newRecentSearchList));
    }

    return (
        <Grid container alignItems="flex-start" className={classes.container}>
            <SearchPage 
                isOldVersion={isOldVersion} 
                isSearching={isSearching} 
                startSearch={startSearch} 
                cancelSearch={cancelSearch}
                doSearch={doSearch}
                removeRecentSearch={removeRecentSearch}
            />
            {!isSearching && (
                <Typography>Oh no, the page canâ€™t be found</Typography>
            )}
        </Grid>
    );
}

export default FacilitiesPage;
