import React, { useEffect, useRef, useState } from 'react';
import { makeStyles, useTheme } from '@mui/styles';
import { styled, alpha } from '@mui/material/styles';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import SearchIcon from '@mui/icons-material/Search';
import { Button, Grid, InputBase } from '@mui/material';

const useStyles = makeStyles(theme => ({
    icon: {
        color: theme.palette.background.darkGrey
    }
}));

function SearchListButtonV1({ isSearching, startSearch, cancelSearch }) {
    // search bar -> click in
    // choose sport or venue will pop up list
    // allow to cancel to go back
    // can make use of state/? to check if is searching

    const classes = useStyles();
    const location = useLocation();
    const navigate = useNavigate();


    return (
        <Grid container justifyContent="space-between" alignItems="center">
            <Grid item xs={isSearching ? 10 : 12}>
                <Search onClick={startSearch}>
                    <SearchIconWrapper>
                        <SearchIcon fontSize="large" className={classes.icon} />
                    </SearchIconWrapper>
                    <StyledInputBase
                        fullWidth
                        placeholder={isSearching ? 'Enter Sport / Venue' : 'Search Facilities'}
                        inputProps={{ 'aria-label': 'search' }}
                    />
                </Search>
            </Grid>
            <Grid item>
                {isSearching && (
                    <CancelButton variant="text" onClick={cancelSearch}>Cancel</CancelButton>
                )}
            </Grid>
        </Grid>
    );
}

export default SearchListButtonV1;
