import React, { useEffect, useRef, useState } from 'react';
import { makeStyles } from '@mui/styles';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import Paper from '@mui/material/Paper';
import BottomNavigation from '@mui/material/BottomNavigation';
import BottomNavigationAction from '@mui/material/BottomNavigationAction';
import { pages } from '../resources/constants';

const useStyles = makeStyles(theme => ({
    button: {
        backgroundColor: theme.palette.primary.main,
        borderRadius: theme.spacing(4),
        height: theme.spacing(5),
        color: theme.palette.primary.dark,
        '&:hover': {
            backgroundColor: theme.palette.primary.dark,
            color: theme.palette.primary.contrastText
        },
    },
}));

function NavigationBar() {
    const classes = useStyles();
    const location = useLocation();
    let navigate = useNavigate();

    const [value, setValue] = useState(0);
    // useEffect(() => {
    //     ref.current.ownerDocument.body.scrollTop = 0;
    // }, [value]);

    return (
        <Paper sx={{ position: 'fixed', bottom: 0, left: 0, right: 0 }} elevation={2}>
            <BottomNavigation
                showLabels
                value={value}
                onChange={(event, newValue) => {
                    console.log(newValue, pages);
                    setValue(newValue);
                    navigate(pages[newValue].href);
                }}
            >
                {
                    pages.map(page => {
                        return <BottomNavigationAction label={page.label} icon={page.icon} />
                    })
                }
            </BottomNavigation>
        </Paper>
    );
}

export default NavigationBar;
