import React, { useEffect, useRef, useState } from 'react';
import { makeStyles, useTheme } from '@mui/styles';
import { styled, alpha } from '@mui/material/styles';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import SearchIcon from '@mui/icons-material/Search';
import { Button, Grid, InputBase } from '@mui/material';

const useStyles = makeStyles(theme => ({
    icon: {
        color: theme.palette.background.darkGrey
    },
    my2: {
        marginLeft: theme.spacing(2),
        marginRight: theme.spacing(2)
    }
}));

const Search = styled('div')(({ theme }) => ({
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    outline: `1px solid ${theme.palette.background.midGrey}`,
    // backgroundColor: alpha(theme.palette.background.lightGrey, 0.5),
    // '&:hover': {
    //   backgroundColor: alpha(theme.palette.background.lightGrey, 0.75),
    // },
    width: '100%'
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
    color: theme.palette.background.darkGrey,
    '& .MuiInputBase-input': {
        padding: theme.spacing(0.75, 1),
        // vertical padding + font size from searchIcon
        paddingLeft: `calc(1em + ${theme.spacing(4)})`,
        // transition: theme.transitions.create('width'),
        width: '100%',
    },
}));

const CancelButton = styled(Button)(({ theme }) => ({
    color: theme.palette.text.secondary,
    textTransform: 'capitalize',
    fontWeight: 400,
    fontSize: '1.125rem',
    padding: 0,
    minWidth: '4rem'
  }));

function SearchBar({ isSearching, startSearch, cancelSearch, doSearch }) {
    const classes = useStyles();
    const location = useLocation();
    const navigate = useNavigate();

    const [value, setValue] = useState("");

    const onEnterSearch = e => {
        setValue(e.target.value)
        if (e.keyCode == 13) {
            console.log(e.target.value);
            doSearch(e.target.value);
        }

    }

    const onCancelSearch = e => {
        cancelSearch();
        console.log(e.target.value);
    }

    return (
        <Grid container justifyContent="space-between" alignItems="center" className={classes.my2}>
            <Grid item xs={isSearching ? 10 : 12}>
                <Search onClick={startSearch} onKeyUp={onEnterSearch}>
                    <SearchIconWrapper>
                        <SearchIcon fontSize="large" className={classes.icon} />
                    </SearchIconWrapper>
                    <StyledInputBase
                        value={value}
                        fullWidth
                        placeholder={isSearching ? 'Enter Sport / Venue' : 'Search Facilities'}
                        inputProps={{ 'aria-label': 'search' }}
                    />
                </Search>
            </Grid>
            <Grid item>
                {isSearching && (
                    <CancelButton variant="text" onClick={onCancelSearch}>Cancel</CancelButton>
                )}
            </Grid>
        </Grid>
    );
}

export default SearchBar;
